$(function(){var t=$(".notes").isotope({itemSelector:".note"});$(".filter-container .btn").click(function(e){var n=$(this);e.preventDefault();var i=n.attr("data-filter");t.isotope({filter:i}),$(".filter-container .btn").removeClass("btn-active"),n.addClass("btn-active")});var e=$("#searchNote").on("keyup",function(){var n=e.val();t.isotope({filter:function(){var t=$(this).find(".note-tags").text();return t.indexOf(n)>=0}})})});