$(function(){var t=$(".notes").isotope({itemSelector:".note",getSortData:{number:".note-number parseInt",title:".note-title",category:"[data-category]",date:function(t){return Date.parse($(t).find(".note-date").text())}}});$("#sortNotes").change(function(){var e=this.value,n=$("#sortDescending:checked").length>0;t.isotope({sortBy:e,sortAscending:!n})}),$("#sortDescending").click(function(){$("#sortNotes").triggerHandler("change")})});